  selector: 'app-aboutme-Introduction',
  template: `


    <mat-progress-bar 
        [mode]="mode"
        [value]="value"
        [bufferValue]="bufferValue"
        [color]="color">
    </mat-progress-bar>


    <div class="markdown">
      <markdown
        class="variable-binding"
        [src]="'../../assets/Introduction.md'"
